import React, { Component, Fragment } from 'react';

class CardProduct extends Component {
    state = {
        order: 4,
        name: "Rino",
    }
    handleCounterChange = () => {
        this.props.onCounterChange(5);
    }
    handlePlus = () => {
        this.setState({
            order: this.state.order + 1
        })
        this.handleCounterChange();
    }
    handleMin = () => {
        if (this.state.order > 0) {
            this.setState({
                order: this.state.order - 1
            })
        }
    };
    render() {
        return (
            <div className="card">
                <img src="https://etanee-images.s3-ap-southeast-1.amazonaws.com/BSB.jpg" className="fotoAyam" alt="Ayam Seger" />
                <h1>Ayam Seger</h1>
                <p className="price">Rp.50.000</p>
                <p>Ayam Seger Dari Indonesia dari pabrik nya langsung</p>
                <button className="minus" onClick={this.handleMin}>-</button>
                <input type="text" value={this.state.order} className="input" />
                <button className="plus" onClick={this.handlePlus}>+</button>
            </div>

        )
    }
}
export default CardProduct;
