import React, { Component } from 'react';
import Background from './assets/yakult.jpg';
import './App.css';
class App extends Component {
  render() {
    return (
      <div style={styles.wrapper}>
        <div style={styles.parent}>
          {/* <img src={fotoGans} alt=""
            width={172}
            height={172}
          /> */}
          <div style={styles.parentText}>
            <h1 style={{
              textAlign: "center",
              color: "#ff9100",
              fontWeight: "bold",

            }}>Cintai Usus Mu</h1>
            <h1 style={{
              color: "#f59a22",
            }}> Minum Yakult Tiap Hari</h1>
            <span style={{
              marginLeft: 80,
              fontSize: 12,
              color: "blue"
            }}>Jangan Lupa Minum Yakult Hari ini</span>
          </div>
        </div>

      </div>
    )
  }
}
let h = window.innerHeight;

const styles = {
  wrapper: {
    display: "flex",
    backgroundImage: `url(${Background})`,
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
    height: "100%",
    backgroundPosition: "center",
  },
  parent: {
    height: h,
    width: "100%",
    display: "flex",
  },
  parentText: {
    margin: "auto",
  },
}

export default App;
