import React, { Component } from 'react';
import Background from './bg1.png';
class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      matches: window.matchMedia("(min-width: 648px)").matches,
      email: ""
    };
  }
  componentDidMount() {
    const handler = e => this.setState({ matches: e.matches });
    window.matchMedia("(min-width: 648px)").addListener(handler);
  }
  render() {
    return (
      <div style={styles.wrapper}>
        <div style={styles.parent}>
          <div style={styles.quotes}>
            <h1 style={{
              fontFamily: "Pangolin",
              fontWeight: "normal",
              marginTop: 180,
              marginLeft: 100,
            }}>Save Your Earth</h1>

            <h3 style={{
              marginLeft: 100,
              fontFamily: "pangolin",
              fontWeight: "normal",
            }}>Menanam Satu Pohon, Menghasilkan Sejuta Manfaat</h3>
          </div>
        </div>
      </div>
    )
  }
}
let h = window.innerHeight;

const styles = {
  wrapper: {
    display: "flex",
    backgroundImage: `url(${Background})`,
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
    height: "100%",
    backgroundPosition: "center",
  },
  parent: {
    height: h,
    width: "100%",
    display: "flex",
  },
  quotes: {
    margin: 40,
  },
  parentIcon: {
    margin: "auto",
  }
}

export default App;
