import React, { Component, Fragment } from 'react';
import './product.css';
import CardProduct from '../CardProduct/CardProduct';
class product extends Component {
    state = {
        order: 4,
        name: "Rino",
    }

    handlePlus = () => {
        this.setState({
            order: this.state.order + 1
        })
    }
    handleMin = () => {
        if (this.state.order > 0) {
            this.setState({
                order: this.state.order - 1
            })
        }
    };
    render() {
        return (
            <Fragment>
                <div className="header">
                    <h1>Etanee</h1>
                    <div className="troly">
                        <img src="https://www.etanee.id/img/icon/ic_cart_black.svg" className="icon-keranjang" alt="icon" />
                        <div className="count"> {this.state.order}
                        </div>
                    </div>
                </div>
                <CardProduct />
            </Fragment>
        )
    }
}
export default product;
