import React, { Component } from 'react';
import logo from './icon1.png';
import Background from './bg1.png';
class App extends Component {
  render() {
    return (
      <div style={styles.wrapper}>
        <div style={styles.parent}>
          <div style={styles.quotes}>
            <h1 style={{
              fontFamily: "Pangolin",
              fontWeight: "normal",
              marginTop: 180,
              marginLeft: 100,

            }}>Save Your Earth</h1>

            <h3 style={{
              marginLeft: 100,
              fontFamily: "pangolin",
              fontWeight: "normal",
            }}>Menanam Satu Pohon, Menghasilkan Sejuta Manfaat</h3>
            <div style={{ display: "flex", flexDirection: "row", alignSelf: "center" }}>
              <div style={styles.parentButton}>
                <div style={styles.Readmore}>
                  Readmore
                        </div>
              </div>
              <div style={styles.parentButton1}>
                <div style={styles.Readmore1}>
                  Readmore
                        </div>
              </div>
            </div>
          </div>
        </div>
      </div >
    )
  }
}
let h = window.innerHeight;

const styles = {
  wrapper: {
    display: "flex",
    backgroundImage: `url(${Background})`,
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
    height: "100%",
    backgroundPosition: "center",
  },
  parent: {
    height: h,
    width: "100%",
    display: "flex",
  },
  quotes: {
    margin: 40,
  },
  parentButton: {
    background: "#00B894",
    height: "8%",
    width: "30%",
    borderRadius: "5%",
    margin: "auto",
    marginRight: 260,
    fontSize: 14,
    fontFamily: "pangolin",
    color: "#FFF",
  },
  color: "#ffff",
  margin: 14,
  marginRight: 50,
  marginLeft: 20,
}

export default App;
