import React, { Component } from 'react';
import logo from './icon1.png';
import Background from './bg1.png';
class App extends Component {
  render() {
    return (
      <div style={styles.wrapper}>
        <div style={styles.parent}>
          <div style={styles.quotes}>
            <h1 style={{
              fontFamily: "Pangolin",
              fontWeight: "normal",
              marginTop: 180,
              marginLeft: 100,

            }}>Save Your Earth</h1>

            <h3 style={{
              marginLeft: 100,
              fontFamily: "pangolin",
              fontWeight: "normal",
            }}>Menanam Satu Pohon, Menghasilkan Sejuta Manfaat</h3>
            <div style={styles.parentButton}>

              <img src={logo}
                style={{
                  margin: 5,
                  marginTop: 10,
                }}
                alt="" />
              Donasi Pohon
           </div>
          </div>
        </div>
      </div>
    )
  }
}
let h = window.innerHeight;

const styles = {
  wrapper: {
    display: "flex",
    backgroundImage: `url(${Background})`,
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
    height: "100%",
    backgroundPosition: "center",
  },
  parent: {
    height: h,
    width: "100%",
    display: "flex",
  },
  quotes: {
    margin: 40,
  },
  parentButton: {
    background: "#00B894",
    height: "8%",
    width: "30%",
    borderRadius: "5%",
    margin: "auto",
    marginRight: 258,
    fontSize: 14,
    fontFamily: "pangolin",
    color: "#FFF",
  }
}

export default App;
